using System;\nusing System.Drawing;\n\npublic class ColorTint\n{\n    public static Color TintColor(Color color, float percent)\n    {\n        // Ensure percent is between 0 and 100\n        percent = Math.Max(0, Math.Min(100, percent));\n        \n        // Convert percent to a fraction\n        float fraction = percent / 100f;\n        \n        // Calculate the new RGB values\n        int r = (int)((1 - fraction) * color.R + fraction * 255);\n        int g = (int)((1 - fraction) * color.G + fraction * 255);\n        int b = (int)((1 - fraction) * color.B + fraction * 255);\n        \n        // Return the tinted color\n        return Color.FromArgb(r, g, b);\n    }\n\n    public static string ColorToHex(Color color)\n    {\n        return $"#{color.R:X2}{color.G:X2}{color.B:X2}";\n    }\n\n    public static void Main()\n    {\n        Color originalColor = Color.FromArgb(100, 150, 200);\n        float tintPercent = 90; // adjust this percentage as needed\n\n        Color tintedColor = TintColor(originalColor, tintPercent);\n        string hexColor = ColorToHex(tintedColor);\n        \n        Console.WriteLine($"Original Color: #{originalColor.R:X2}{originalColor.G:X2}{originalColor.B:X2}");\n        Console.WriteLine($"Tinted Color: {hexColor}");\n    }\n}