using System;\nusing System.Web.UI;\nusing System.Web.UI.WebControls;\nusing System.Web.UI.WebControls.WebParts;\nusing System.Web.UI.ScriptControls;\n\n[ToolboxData("<{0}:CustomControl runat=server></{0}:CustomControl>")]\npublic class CustomControl : ScriptControl\n{\n    protected override void OnLoad(EventArgs e)\n    {\n        base.OnLoad(e);\n    }\n\n    protected override IEnumerable<ScriptDescriptor> GetScriptDescriptors()\n    {\n        ScriptControlDescriptor descriptor = new ScriptControlDescriptor("MyNamespace.CustomControl", this.ClientID);\n        return new ScriptDescriptor[] { descriptor };\n    }\n\n    protected override IEnumerable<ScriptReference> GetScriptReferences()\n    {\n        ScriptReference reference = new ScriptReference("~/Scripts/CustomControl.js");\n        return new ScriptReference[] { reference };\n    }\n}