public static class MyStaticClass\n{\n    public static object GetDataFromCache(string cacheKey)\n    {\n        return HttpContext.Current.Cache[cacheKey];\n    }\n\n    public static void SetDataInCache(string cacheKey, object data, DateTime absoluteExpiration)\n    {\n        HttpContext.Current.Cache.Insert(cacheKey, data, null, absoluteExpiration, System.Web.Caching.Cache.NoSlidingExpiration);\n    }\n}