using System;\nusing System.Data.SqlClient;\nusing System.Linq;\n\npublic void BindGridView(List<int> itemIDs)\n{\n    // Assuming you have your connection string stored in a variable named connectionString\n    string connectionString = "your_connection_string_here";\n\n    // Create the SQL query dynamically\n    string query = "SELECT [ItemID], [Name], [RelDate], [Price], [Status] FROM [item_k] WHERE [ItemID] IN (" + \n                   string.Join(",", itemIDs.Select((id, index) => "@id" + index)) + ")";\n\n    // Create SQL connection\n    using (SqlConnection conn = new SqlConnection(connectionString))\n    {\n        SqlCommand cmd = new SqlCommand(query, conn);\n\n        // Add parameters to the command\n        for (int i = 0; i < itemIDs.Count; i++)\n        {\n            cmd.Parameters.AddWithValue("@id" + i, itemIDs[i]);\n        }\n\n        // Open connection\n        conn.Open();\n\n        // Execute the query and bind the result to GridView\n        SqlDataReader reader = cmd.ExecuteReader();\n        GridView1.DataSource = reader;\n        GridView1.DataBind();\n\n        // Close connection\n        conn.Close();\n    }\n}