using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string microsoftHtml = @"<p class=MsoNormal style='margin-bottom:6.0pt'>This is some text.</p>\n                                <span style='mso-spacerun:yes'>       </span>\n                                <o:p></o:p><b>Bold Text</b><o:p></o:p>";\n\n        string cleanedHtml = CleanMicrosoftHtml(microsoftHtml);\n        Console.WriteLine(cleanedHtml);\n    }\n\n    static string CleanMicrosoftHtml(string html)\n    {\n        // Remove MS Office specific tags\n        string pattern = @"<\s*o:p\s*[^>]*>(.*?)<\s*/\s*o:p\s*>";\n        html = Regex.Replace(html, pattern, string.Empty, RegexOptions.IgnoreCase);\n\n        // Remove MS Office specific styles\n        pattern = @"<\s*span\s*style=['""]mso.*?['""].*?>\s*<\s*/\s*span\s*>";\n        html = Regex.Replace(html, pattern, string.Empty, RegexOptions.IgnoreCase);\n\n        // Remove class Mso* attributes\n        pattern = @"class\s*=\s*['""]Mso.*?['""]";\n        html = Regex.Replace(html, pattern, string.Empty, RegexOptions.IgnoreCase);\n\n        // Remove style attributes that are MS specific\n        pattern = @"style\s*=\s*['""]mso.*?['""]";\n        html = Regex.Replace(html, pattern, string.Empty, RegexOptions.IgnoreCase);\n\n        // Additional cleaning as needed\n        pattern = @"<\s*style[^>]*>(.*?)<\s*/\s*style\s*>";\n        html = Regex.Replace(html, pattern, string.Empty, RegexOptions.IgnoreCase);\n\n        // Remove empty tags\n        pattern = @"<[^/>]+>\s*</[^>]+>";\n        html = Regex.Replace(html, pattern, string.Empty, RegexOptions.IgnoreCase);\n\n        return html;\n    }\n}