using System;\nusing System.Windows.Forms;\n\npublic class TouchFriendlyForm : Form\n{\n    private Button touchButton;\n\n    public TouchFriendlyForm()\n    {\n        // Initialize the touch button\n        touchButton = new Button\n        {\n            Text = "Touch Me",\n            Width = 200,\n            Height = 100,\n            Font = new System.Drawing.Font("Arial", 16),\n            Location = new System.Drawing.Point(50, 50)\n        };\n\n        // Add the button to the form\n        this.Controls.Add(touchButton);\n    }\n\n    [STAThread]\n    static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new TouchFriendlyForm());\n    }\n}