using System;\nusing System.Web;\nusing System.Data.SqlClient;\n\npublic class AuthHelper\n{\n    public static bool ValidateUser(HttpRequest request)\n    {\n        HttpCookie authCookie = request.Cookies["AuthCookie"];\n        if (authCookie != null)\n        {\n            string userName = authCookie["UserName"];\n            string authToken = authCookie["AuthToken"];\n            \n            if (!string.IsNullOrEmpty(userName) && !string.IsNullOrEmpty(authToken))\n            {\n                // Validate against the database\n                using (SqlConnection conn = new SqlConnection("your_connection_string"))\n                {\n                    conn.Open();\n                    SqlCommand cmd = new SqlCommand("SELECT COUNT(*) FROM Users WHERE UserName = @UserName AND AuthToken = @AuthToken", conn);\n                    cmd.Parameters.AddWithValue("@UserName", userName);\n                    cmd.Parameters.AddWithValue("@AuthToken", authToken);\n                    \n                    int result = (int)cmd.ExecuteScalar();\n                    \n                    return result > 0;\n                }\n            }\n        }\n        return false;\n    }\n}