public MyType GetMyTypes(string criteria)\n{\n    try\n    {\n        return GetMyTypes();\n    }\n    catch (Exception ex)\n    {\n        OutgoingWebResponseContext response = WebOperationContext.Current.OutgoingResponse;\n        response.StatusCode = HttpStatusCode.Forbidden;\n        response.ContentType = "application/json";\n\n        ErrorDetail errorDetail = new ErrorDetail\n        {\n            Message = "A Big fat error occurred",\n            Detail = ex.Message\n        };\n\n        // Serialize the error detail to JSON\n        string jsonError = new DataContractJsonSerializer(typeof(ErrorDetail)).WriteObjectToString(errorDetail);\n\n        // Set the response entity body to the JSON error detail\n        response.Write(jsonError);\n\n        // Return null as the service operation result\n        return null;\n    }\n}