using System;\nusing System.IO;\nusing System.Net;\nusing System.Web.Services.Protocols;\nusing YourProjectNamespace.ReportServer2005;\n\npublic class ReportExporter\n{\n    public void ExportReportToPDF(string reportPath, string ticketNumber)\n    {\n        // Create an instance of the reporting service\n        ReportExecutionService rs = new ReportExecutionService();\n        rs.Credentials = CredentialCache.DefaultCredentials;\n        rs.Url = "http://<YourReportServer>/ReportServer/ReportExecution2005.asmx";\n\n        // Define report and parameters\n        string historyID = null;\n        string deviceInfo = null;\n        string format = "PDF";\n        Byte[] results;\n        string encoding = String.Empty;\n        string mimeType = String.Empty;\n        string extension = String.Empty;\n        Warning[] warnings = null;\n        ParameterValue[] reportHistoryParameters = null;\n        ParameterValue[] parameters = new ParameterValue[1];\n\n        parameters[0] = new ParameterValue();\n        parameters[0].Name = "TicketNumber";\n        parameters[0].Value = ticketNumber;\n\n        ExecutionInfo execInfo = new ExecutionInfo();\n        ExecutionHeader execHeader = new ExecutionHeader();\n\n        rs.ExecutionHeaderValue = execHeader;\n        execInfo = rs.LoadReport(reportPath, historyID);\n\n        rs.SetExecutionParameters(parameters, "en-us");\n        String SessionId = rs.ExecutionHeaderValue.ExecutionID;\n\n        try\n        {\n            results = rs.Render(format, deviceInfo, out extension, out encoding, out mimeType, out warnings, out streamIDs);\n\n            // Write the PDF to a file\n            using (FileStream stream = File.Create("output.pdf"))\n            {\n                stream.Write(results, 0, results.Length);\n            }\n            Console.WriteLine("Report exported successfully to output.pdf");\n        }\n        catch (SoapException e)\n        {\n            Console.WriteLine(e.Detail.OuterXml);\n        }\n    }\n}