try\n{\n    SPListItem listItem = // get your SPListItem\n    \n    // Loop backwards to avoid index issues while removing\n    for (int i = listItem.RoleAssignments.Count - 1; i >= 0; --i)\n    { \n        listItem.RoleAssignments.Remove(i); \n    }\n\n    // Update the item if required\n    listItem.Update();\n}\ncatch (SPException ex)\n{\n    // Handle specific SharePoint exceptions\n    Console.WriteLine("SharePoint Exception: " + ex.Message);\n}\ncatch (Exception ex)\n{\n    // Handle general exceptions\n    Console.WriteLine("Exception: " + ex.Message);\n}