using System;\nusing System.ServiceModel;\nusing System.ServiceModel.Channels;\n\npublic class SecureServiceClient\n{\n    public void CallWebService()\n    {\n        // Define the binding\n        var binding = new BasicHttpBinding(BasicHttpSecurityMode.Transport)\n        {\n            Security = { Transport = { ClientCredentialType = HttpClientCredentialType.Basic } }\n        };\n\n        // Define the endpoint address\n        var endpointAddress = new EndpointAddress("https://example.com/Service.svc");\n\n        // Create the channel factory\n        var factory = new ChannelFactory<IMyService>(binding, endpointAddress);\n\n        // Set client credentials\n        factory.Credentials.UserName.UserName = "yourUsername";\n        factory.Credentials.UserName.Password = "yourPassword";\n\n        // Create the service client\n        var client = factory.CreateChannel();\n\n        try\n        {\n            // Call the service method\n            var result = client.MyServiceMethod();\n            Console.WriteLine("Service call successful: " + result);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Service call failed: " + ex.Message);\n        }\n        finally\n        {\n            // Close the client\n            ((IClientChannel)client).Close();\n        }\n    }\n}\n\n[ServiceContract]\npublic interface IMyService\n{\n    [OperationContract]\n    string MyServiceMethod();\n}