// In your Global.asax or Startup.cs\nprotected void Application_BeginRequest()\n{\n    HttpContext.Current.Response.AddHeader("Access-Control-Allow-Origin", "*");\n    HttpContext.Current.Response.AddHeader("Access-Control-Allow-Methods", "POST, GET, OPTIONS");\n    HttpContext.Current.Response.AddHeader("Access-Control-Allow-Headers", "Content-Type, Authorization");\n    \n    if (HttpContext.Current.Request.HttpMethod == "OPTIONS")\n    {\n        HttpContext.Current.Response.StatusCode = 204;\n        HttpContext.Current.Response.End();\n    }\n}