using System;\nusing TagLib;\n\npublic class MediaMetadata\n{\n    public string FileType { get; private set; }\n    public string Title { get; private set; }\n    public string Album { get; private set; }\n    public string Artist { get; private set; }\n    public TimeSpan Duration { get; private set; }\n    public int Bitrate { get; private set; }\n    public int SampleRate { get; private set; }\n    public int Channels { get; private set; }\n    public int Width { get; private set; }\n    public int Height { get; private set; }\n\n    public MediaMetadata(string filePath)\n    {\n        var file = File.Create(filePath);\n\n        FileType = file.MimeType;\n        Title = file.Tag.Title;\n        Album = file.Tag.Album;\n        Artist = file.Tag.FirstArtist;\n        Duration = file.Properties.Duration;\n        Bitrate = file.Properties.AudioBitrate;\n        SampleRate = file.Properties.AudioSampleRate;\n        Channels = file.Properties.AudioChannels;\n\n        if (file.Properties.VideoWidth > 0 && file.Properties.VideoHeight > 0)\n        {\n            Width = file.Properties.VideoWidth;\n            Height = file.Properties.VideoHeight;\n        }\n    }\n\n    public override string ToString()\n    {\n        return $"FileType: {FileType}\n" +\n               $"Title: {Title}\n" +\n               $"Album: {Album}\n" +\n               $"Artist: {Artist}\n" +\n               $"Duration: {Duration}\n" +\n               $"Bitrate: {Bitrate} kbps\n" +\n               $"SampleRate: {SampleRate} Hz\n" +\n               $"Channels: {Channels}\n" +\n               $"Resolution: {Width}x{Height}";\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static void Main()\n    {\n        string filePath = "path_to_your_media_file";\n        var metadata = new MediaMetadata(filePath);\n        Console.WriteLine(metadata);\n    }\n}