using Oracle.ManagedDataAccess.Client;\n\npublic void GetEmployeeDetails(int employeeId)\n{\n    string connString = "User Id=myUsername;Password=myPassword;Data Source=myDataSource";\n    using (OracleConnection conn = new OracleConnection(connString))\n    {\n        using (OracleCommand cmd = new OracleCommand("GetEmployeeDetails", conn))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n\n            cmd.Parameters.Add("p_employee_id", OracleDbType.Int32).Value = employeeId;\n            cmd.Parameters.Add("p_employee_name", OracleDbType.Varchar2, 50).Direction = ParameterDirection.Output;\n            cmd.Parameters.Add("p_employee_salary", OracleDbType.Decimal).Direction = ParameterDirection.Output;\n\n            conn.Open();\n            cmd.ExecuteNonQuery();\n\n            string employeeName = cmd.Parameters["p_employee_name"].Value.ToString();\n            decimal employeeSalary = Convert.ToDecimal(cmd.Parameters["p_employee_salary"].Value);\n\n            // Use the retrieved employee details as needed\n        }\n    }\n}