using System;\nusing System.IO;\nusing System.Text;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Example MemoryStream containing a string\n        byte[] byteArray = Encoding.UTF8.GetBytes("Hello, World!");\n        using (MemoryStream memoryStream = new MemoryStream(byteArray))\n        {\n            // Convert MemoryStream to string\n            string result = ConvertMemoryStreamToString(memoryStream);\n            Console.WriteLine(result);\n        }\n    }\n\n    public static string ConvertMemoryStreamToString(MemoryStream memoryStream)\n    {\n        memoryStream.Position = 0; // Ensure the position is at the beginning\n        using (StreamReader reader = new StreamReader(memoryStream, Encoding.UTF8))\n        {\n            return reader.ReadToEnd();\n        }\n    }\n}