protected void btnSave_Click(object sender, EventArgs e)\n     {\n         var context = GlobalHost.ConnectionManager.GetHubContext<ProgressHub>();\n         var filesToSave = GetFilesToSave();\n         int totalFiles = filesToSave.Count;\n         int processedFiles = 0;\n\n         foreach (var fileToSave in filesToSave)\n         {\n             SaveProcessedFile(fileToSave); // It takes almost 30 seconds to save a file\n             processedFiles++;\n             string message = $"Processed {processedFiles}/{totalFiles} files.";\n             context.Clients.All.updateProgress(message); // Send progress updates to the client\n         }\n     }