// 64-bit long values\nlong a = 0x123456789ABCDEF0L;\nlong b = 0x0FEDCBA987654321L;\n\n// On a 32-bit processor, this operation is broken down\n// into handling two 32-bit parts for each long value.\nint a_low = (int)(a & 0xFFFFFFFFL);\nint a_high = (int)(a >> 32);\nint b_low = (int)(b & 0xFFFFFFFFL);\nint b_high = (int)(b >> 32);\n\n// Perform the addition separately for low and high parts\nint result_low = a_low + b_low;\nint result_high = a_high + b_high + (result_low < a_low ? 1 : 0);\n\n// Combine the results back into a single 64-bit value\nlong result = ((long)result_high << 32) | (result_low & 0xFFFFFFFFL);