@page "/catalog"\n   @inject HttpClient Http\n\n   <div>\n       <TreeComponent OnSelect="LoadDetails" />\n       <DetailsComponent @ref="detailsComponent" />\n   </div>\n\n   @code {\n       private DetailsComponent detailsComponent;\n\n       private async Task LoadDetails(int id)\n       {\n           var details = await Http.GetFromJsonAsync<Details>("api/catalog/details/" + id);\n           detailsComponent.Load(details);\n       }\n   }