using System;\nusing NATUPNPLib;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            // Create the UPnP NAT object\n            UPnPNAT upnpnat = new UPnPNAT();\n            IStaticPortMappingCollection mappings = upnpnat.StaticPortMappingCollection;\n\n            if (mappings != null)\n            {\n                // Add a port mapping\n                mappings.Add(12345, "TCP", 12345, "192.168.1.2", true, "My Application");\n\n                Console.WriteLine("Port mapping added successfully.");\n            }\n            else\n            {\n                Console.WriteLine("No UPnP device found.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n        }\n    }\n}