using System;\nusing System.Collections.Generic;\nusing System.ComponentModel.DataAnnotations;\nusing System.ComponentModel.DataAnnotations.Schema;\nusing System.Data.Entity;\nusing System.Xml.Serialization;\n\npublic class MyEntity\n{\n    [Key]\n    public int Id { get; set; }\n\n    public string Name { get; set; }\n\n    [XmlIgnore] // Prevents cycles during serialization\n    public virtual ICollection<MyRelatedEntity> RelatedEntities { get; set; }\n}\n\npublic class MyRelatedEntity\n{\n    [Key]\n    public int Id { get; set; }\n\n    public string Description { get; set; }\n\n    [ForeignKey("MyEntityId")]\n    public virtual MyEntity MyEntity { get; set; }\n\n    public int MyEntityId { get; set; }\n}\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<MyEntity> MyEntities { get; set; }\n    public DbSet<MyRelatedEntity> MyRelatedEntities { get; set; }\n}