using System;\nusing System.Runtime.InteropServices;\n\npublic class HfmClient : IDisposable\n{\n    private HfmComObject _comObject;\n\n    public void Connect()\n    {\n        // Instantiate and use your COM object\n        _comObject = new HfmComObject();\n    }\n\n    public void Disconnect()\n    {\n        if (_comObject != null)\n        {\n            Marshal.FinalReleaseComObject(_comObject);\n            _comObject = null;\n        }\n    }\n\n    public void Dispose()\n    {\n        Disconnect();\n        GC.SuppressFinalize(this);\n    }\n\n    ~HfmClient()\n    {\n        Disconnect();\n    }\n}\n\npublic class HfmComObject\n{\n    // Your COM object implementation\n}