using System.IO;\nusing System.Xml.Serialization;\n\nclass Program\n{\n    static void Main()\n    {\n        using (var context = new MyDbContext())\n        {\n            // Assuming data is already populated in the database\n            var entity = context.MyEntities.Include("RelatedEntities").FirstOrDefault();\n\n            if (entity != null)\n            {\n                XmlSerializer serializer = new XmlSerializer(typeof(MyEntity));\n                using (StringWriter writer = new StringWriter())\n                {\n                    serializer.Serialize(writer, entity);\n                    string serializedXml = writer.ToString();\n                    Console.WriteLine(serializedXml);\n                }\n            }\n        }\n    }\n}