using System.Web.Mvc;\nusing System.IO;\n\npublic class HomeController : Controller\n{\n    [HttpPost]\n    public ActionResult SaveForm(string formData)\n    {\n        // Save the form data to a file or database\n        string path = Server.MapPath("~/App_Data/form.json");\n        System.IO.File.WriteAllText(path, formData);\n        \n        return Json(new { success = true });\n    }\n}