using Gtk;\nusing System;\n\npublic class NotebookExample : Window\n{\n    private Notebook notebook;\n\n    public NotebookExample() : base("Notebook Example")\n    {\n        SetDefaultSize(400, 300);\n        SetPosition(WindowPosition.Center);\n\n        notebook = new Notebook();\n        Add(notebook);\n\n        for (int i = 0; i < 3; i++)\n        {\n            VBox vbox = new VBox();\n            Button button = new Button($"Close Tab {i}");\n            button.Clicked += OnCloseTabClicked;\n            vbox.PackStart(new Label($"Content of Tab {i}"), true, true, 0);\n            vbox.PackStart(button, false, false, 0);\n\n            notebook.AppendPage(vbox, new Label($"Tab {i}"));\n        }\n\n        ShowAll();\n    }\n\n    private void OnCloseTabClicked(object sender, EventArgs e)\n    {\n        Widget buttonWidget = sender as Widget;\n\n        if (buttonWidget != null)\n        {\n            Widget currentWidget = buttonWidget;\n            while (currentWidget != null && !(currentWidget is Notebook))\n            {\n                currentWidget = currentWidget.Parent;\n            }\n\n            if (currentWidget != null)\n            {\n                int pageNum = notebook.PageNum(currentWidget.Parent);\n                if (pageNum != -1)\n                {\n                    notebook.RemovePage(pageNum);\n                }\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        Application.Init();\n        new NotebookExample();\n        Application.Run();\n    }\n}