using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing Microsoft.SqlServer.Dts.Runtime;\n\npublic void Main()\n{\n    // Connection string to the database\n    string connectionString = "Data Source=your_server;Initial Catalog=your_database;Integrated Security=True;";\n    \n    // Excel file path\n    string excelFilePath = @"C:\path\to\your\excel\file.xlsx";\n    \n    // Query to read SQL statements from Excel\n    string query = "SELECT * FROM [Sheet1$]";\n    \n    using (var excelConnection = new OleDbConnection($"Provider=Microsoft.ACE.OLEDB.12.0;Data Source={excelFilePath};Extended Properties=\"Excel 12.0 Xml;HDR=YES\";"))\n    {\n        excelConnection.Open();\n        \n        using (var command = new OleDbCommand(query, excelConnection))\n        using (var reader = command.ExecuteReader())\n        {\n            using (var sqlConnection = new SqlConnection(connectionString))\n            {\n                sqlConnection.Open();\n                \n                while (reader.Read())\n                {\n                    string sqlStatement = reader["SQLStatementColumn"].ToString();\n\n                    using (var sqlCommand = new SqlCommand(sqlStatement, sqlConnection))\n                    {\n                        sqlCommand.ExecuteNonQuery();\n                    }\n                }\n            }\n        }\n    }\n\n    Dts.TaskResult = (int)ScriptResults.Success;\n}