using System;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class TinyUrlHelper\n{\n    private static readonly HttpClient client = new HttpClient();\n\n    public static async Task<string> CreateTinyUrlAsync(string url)\n    {\n        string requestUrl = $"http://tinyurl.com/api-create.php?url={Uri.EscapeDataString(url)}";\n        HttpResponseMessage response = await client.GetAsync(requestUrl);\n        \n        if (response.IsSuccessStatusCode)\n        {\n            string tinyUrl = await response.Content.ReadAsStringAsync();\n            return tinyUrl;\n        }\n        else\n        {\n            throw new Exception("Failed to create TinyURL.");\n        }\n    }\n}