using System;\nusing System.Collections.Generic;\n\npublic class ArticleEntity\n{\n    public string Text { get; set; }\n}\n\npublic class Program\n{\n    private static List<ArticleEntity> list = new List<ArticleEntity>\n    {\n        new ArticleEntity { Text = "AAA" },\n        new ArticleEntity { Text = "AAB" },\n        new ArticleEntity { Text = "AAC" },\n        new ArticleEntity { Text = "ACC" },\n        new ArticleEntity { Text = "ADA" }\n    };\n\n    private static int FindPositionForArticle(string word)\n    {\n        int left = 0;\n        int right = list.Count;\n\n        while (left < right)\n        {\n            int mid = (left + right) / 2;\n\n            if (string.Compare(list[mid].Text, word, StringComparison.OrdinalIgnoreCase) < 0)\n            {\n                left = mid + 1;\n            }\n            else\n            {\n                right = mid;\n            }\n        }\n\n        return left;\n    }\n\n    public static void Main()\n    {\n        string newWord = "ABB";\n        int position = FindPositionForArticle(newWord);\n        Console.WriteLine($"The best position to insert '{newWord}' is: {position}");\n    }\n}