using PdfSharp.Pdf;\nusing PdfSharp.Drawing;\nusing System.IO;\n\npublic MemoryStream SavePdfToMemoryStream(PdfDocument document)\n{\n    using (var memoryStream = new MemoryStream())\n    {\n        document.Save(memoryStream, false); // Save the document to the MemoryStream\n        return new MemoryStream(memoryStream.ToArray()); // Return a new MemoryStream with the content\n    }\n}