using System;\nusing System.Web.Services.Protocols;\nusing ReportingService2010; // Make sure to add the correct reference to the Reporting Services Web Service\n\npublic class ReportPermissionsChecker\n{\n    public static void Main(string[] args)\n    {\n        // Replace with your SSRS web service URL\n        string reportServiceUrl = "http://your-sql-server/reportserver/ReportService2010.asmx";\n        string reportPath = "/Path/To/Your/Report";\n        string userNameToCheck = "MYDOMAIN\\JimBob";\n\n        ReportingService2010 rs = new ReportingService2010();\n        rs.Url = reportServiceUrl;\n\n        // Use default credentials or specific credentials of the service account\n        rs.Credentials = System.Net.CredentialCache.DefaultCredentials;\n\n        try\n        {\n            bool inheritParent;\n            Policy[] policies = rs.GetPolicies(reportPath, out inheritParent);\n\n            foreach (var policy in policies)\n            {\n                if (string.Equals(policy.GroupUserName, userNameToCheck, StringComparison.OrdinalIgnoreCase))\n                {\n                    Console.WriteLine($"User: {policy.GroupUserName}");\n                    foreach (var role in policy.Roles)\n                    {\n                        Console.WriteLine($"Role: {role.Name}");\n                        Console.WriteLine($"Description: {role.Description}");\n                    }\n                }\n            }\n        }\n        catch (SoapException e)\n        {\n            Console.WriteLine("SOAP Exception: " + e.Detail.InnerXml);\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("Exception: " + e.Message);\n        }\n    }\n}