using System;\nusing System.Reflection;\n\npublic class Program\n{\n    public static void Main()\n    {\n        Type childType = typeof(ChildClass);\n        PropertyInfo childProperty = childType.GetProperty("TempProperty");\n        PropertyInfo baseProperty = childType.BaseType.GetProperty("TempProperty");\n\n        bool isHiding = childProperty.GetMethod.GetBaseDefinition() != baseProperty.GetMethod.GetBaseDefinition();\n\n        Console.WriteLine($"Is ChildClass.TempProperty hiding BaseClass.TempProperty? {isHiding}");\n    }\n}\n\npublic class ChildClass : BaseClass\n{\n    public new virtual string TempProperty { get; set; }\n}\n\npublic class BaseClass\n{\n    public virtual string TempProperty { get; set; }\n}