using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"<Item Type='Book'>\n                        <Param Value='Updike' />\n                        <Param Value='John' />\n                        <Param Value='Toward the End of Time' />\n                       </Item>";\n\n        XPathDocument document = new XPathDocument(new System.IO.StringReader(xml));\n        XPathNavigator navigator = document.CreateNavigator();\n        XPathNodeIterator nodes = navigator.Select("//Item[@Type='Book' and Param[2][@Value='John']]");\n\n        while (nodes.MoveNext())\n        {\n            Console.WriteLine(nodes.Current.OuterXml);\n        }\n    }\n}