string memberId = "a45bd16d-9be0-421b-b5bf-143d334c8155";\n\n// Assuming you have lists of projects and projectMembers\nList<Project> projects = GetProjects(); // Method to get all projects\nList<ProjectMember> projectMembers = GetProjectMembers(); // Method to get all project members\n\nvar result = projects.Where(p => projectMembers\n                                   .Where(pm => pm.MemberId == memberId)\n                                   .Select(pm => pm.ProjectId)\n                                   .Contains(p.ProjectId))\n                     .ToList();