using OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\npublic void hladame_fieldy()\n{\n    // Initialize the WebDriver\n    IWebDriver driver = new ChromeDriver();\n\n    // Navigate to the desired URL\n    driver.Navigate().GoToUrl("http://example.com");\n\n    // Find all input fields of type 'text'\n    var inputFields = driver.FindElements(By.XPath("//input[@type='text']"));\n\n    // Iterate through the input fields and print their names\n    foreach (var inputField in inputFields)\n    {\n        string fieldName = inputField.GetAttribute("name");\n        Console.WriteLine(fieldName);\n    }\n    \n    // Print the total number of input fields\n    Console.WriteLine("Celkovy pocet fieldov je = " + inputFields.Count);\n\n    // Close the WebDriver\n    driver.Quit();\n}