List<string> statuses = new List<string> \n{ \n    "Parts Arr", \n    "NA", \n    "New Call", \n    "Parts Ord", \n    "Parts Req", \n    "F Work"\n}; \n\nvar result = from x in db.tbl_repair_orders\n             where x.CONTRACT == "con" && statuses.Contains(x.STATUS)\n             group x by x.STATUS into grouping\n             select new \n             { \n                 Status = grouping.Key, \n                 Count = grouping.Count() \n             };\n\nreturn result;