using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("SomeLibrary.dll", CharSet = CharSet.Ansi)]\n    public static extern void SomeFunction([MarshalAs(UnmanagedType.LPStr)] string str);\n\n    static void Main()\n    {\n        string nullString = null;\n\n        // This will pass an empty string to the SomeFunction method\n        SomeFunction(nullString);\n    }\n}