using System.Data.SqlClient;\nusing System.Configuration;\n\npublic void ChangeConnectionString(string newConnectionString)\n{\n    // Assuming your dataset is named 'myDataSet'\n    // and your table adapter is named 'myTableAdapter'\n\n    // Create a new SqlConnection object with the new connection string\n    SqlConnection newConnection = new SqlConnection(newConnectionString);\n\n    // Set the new connection to the table adapter\n    myTableAdapter.Connection = newConnection;\n\n    // Optionally, if you use configuration files, you can update the connection string in the config file as well\n    Configuration config = ConfigurationManager.OpenExeConfiguration(ConfigurationUserLevel.None);\n    config.ConnectionStrings.ConnectionStrings["YourConnectionStringName"].ConnectionString = newConnectionString;\n    config.Save(ConfigurationSaveMode.Modified);\n    ConfigurationManager.RefreshSection("connectionStrings");\n}\n\n// Example usage\npublic void UpdateDatabaseConnection()\n{\n    string newConnectionString = "Data Source=NewServer;Initial Catalog=NewDatabase;User ID=NewUser;Password=NewPassword;";\n    ChangeConnectionString(newConnectionString);\n}