using ICSharpCode.SharpZipLib.Zip;\nusing System.IO;\n\npublic void CreateZipFile(string filesPath, string zipName, string zipFilter)\n{\n    FastZip fZip = new FastZip();\n    try\n    {\n        fZip.CreateEmptyDirectories = true;\n        fZip.CreateZip(Path.Combine(filesPath, zipName), filesPath, false, zipFilter);\n    }\n    catch (Exception ex)\n    {\n        // Handle exception appropriately\n        Console.WriteLine(ex.Message);\n    }\n    finally\n    {\n        // Explicitly release resources\n        fZip = null;\n        GC.Collect();\n        GC.WaitForPendingFinalizers();\n    }\n}