using System.Web.UI.WebControls.WebParts;\n\npublic class CustomWebPart : WebPart\n{\n    private string m_myPassword;\n\n    [Personalizable(PersonalizationScope.Shared)]\n    [WebBrowsable(false)]  // Hide the property from the default editor\n    public string MyPassword\n    {\n        get { return m_myPassword; }\n        set { m_myPassword = value; }\n    }\n\n    protected override EditorPartCollection CreateEditorParts()\n    {\n        var baseParts = base.CreateEditorParts();\n        var customPart = new CustomPasswordEditorPart();\n        customPart.ID = this.ID + "_customEditorPart";\n\n        var parts = new EditorPartCollection(new EditorPart[] { customPart });\n        return parts;\n    }\n}