using System.Web.UI.WebControls.WebParts;\nusing System.Web.UI.WebControls;\n\npublic class CustomPasswordEditorPart : EditorPart\n{\n    private TextBox passwordTextBox;\n\n    protected override void CreateChildControls()\n    {\n        passwordTextBox = new TextBox();\n        passwordTextBox.TextMode = TextBoxMode.Password;\n        this.Controls.Add(passwordTextBox);\n    }\n\n    public override bool ApplyChanges()\n    {\n        CustomWebPart webPart = this.WebPartToEdit as CustomWebPart;\n        if (webPart != null)\n        {\n            webPart.MyPassword = passwordTextBox.Text;\n        }\n        return true;\n    }\n\n    public override void SyncChanges()\n    {\n        CustomWebPart webPart = this.WebPartToEdit as CustomWebPart;\n        if (webPart != null)\n        {\n            passwordTextBox.Text = webPart.MyPassword;\n        }\n    }\n}