// Assuming 'data' is your list of data objects fetched from the database\nSession["data"] = data;\n\n// Method to render the table for a specific page\nprivate void RenderTable(int pageIndex, int pageSize)\n{\n    var data = (List<YourDataType>)Session["data"];\n    var paginatedData = data.Skip((pageIndex - 1) * pageSize).Take(pageSize).ToList();\n\n    // Clear existing rows\n    Table.Rows.Clear();\n\n    // Add the rows for the current page\n    foreach (var item in paginatedData)\n    {\n        TableRow row = new TableRow();\n        TableCell firstCell = new TableCell();\n        TableCell secondCell = new TableCell();\n        TableCell thirdCell = new TableCell();\n\n        Label lbl1 = new Label { Text = item.Property1.ToString() };\n        Label lbl2 = new Label { Text = item.Property2.ToString() };\n        Label lbl3 = new Label { Text = item.Property3.ToString() };\n\n        firstCell.Controls.Add(lbl1);\n        secondCell.Controls.Add(lbl2);\n        thirdCell.Controls.Add(lbl3);\n\n        row.Cells.Add(firstCell);\n        row.Cells.Add(secondCell);\n        row.Cells.Add(thirdCell);\n\n        Table.Rows.Add(row);\n    }\n\n    // Render pagination controls\n    RenderPaginationControls(pageIndex, pageSize, data.Count);\n}\n\nprivate void RenderPaginationControls(int pageIndex, int pageSize, int totalRecords)\n{\n    int totalPages = (int)Math.Ceiling((double)totalRecords / pageSize);\n\n    // Clear existing pagination controls\n    PaginationControls.Controls.Clear();\n\n    for (int i = 1; i <= totalPages; i++)\n    {\n        LinkButton pageLink = new LinkButton\n        {\n            Text = i.ToString(),\n            CommandArgument = i.ToString()\n        };\n        pageLink.Click += PageLink_Click;\n        PaginationControls.Controls.Add(pageLink);\n    }\n}\n\nprivate void PageLink_Click(object sender, EventArgs e)\n{\n    int pageIndex = int.Parse((sender as LinkButton).CommandArgument);\n    int pageSize = 10; // Define your page size here\n\n    RenderTable(pageIndex, pageSize);\n}