// Assuming you have a resource file named ValidationMessages.resx\nstring errorMessage = Resources.ValidationMessages.InvalidDateMessage;\n\npublic ActionResult YourAction(DateTime? yourDate)\n{\n    if (!ModelState.IsValid)\n    {\n        if (ModelState.ContainsKey("yourDate") && ModelState["yourDate"].Errors.Count > 0)\n        {\n            // Clear the existing errors for the field\n            ModelState["yourDate"].Errors.Clear();\n\n            // Add your localized error message\n            ModelState.AddModelError("yourDate", errorMessage);\n        }\n    }\n\n    // Continue with your logic\n    return View();\n}