<%@ Page Language="C#" AutoEventWireup="true" CodeFile="LetterTemplate.aspx.cs" Inherits="LetterTemplate" %>\n<!DOCTYPE html>\n<html>\n<head runat="server">\n    <title>Letter Template</title>\n</head>\n<body>\n    <form id="form1" runat="server">\n        <div>\n            <p>Dear <%: GetContent("ForeName") %> <%: GetContent("SureName") %>,</p>\n            <p>Address:</p>\n            <p><%: GetContent("AddressLine1") %></p>\n            <p><%: GetContent("AddressLine2") %></p>\n            <p><%: GetContent("City") %>, <%: GetContent("State") %> <%: GetContent("ZipCode") %></p>\n        </div>\n    </form>\n</body>\n</html>