public void RenderExcelFile(string filePath)\n{\n    // Check if the file exists\n    if (File.Exists(filePath))\n    {\n        // Clear the current response\n        HttpContext.Current.Response.Clear();\n\n        // Set the content type to Excel\n        HttpContext.Current.Response.ContentType = "application/vnd.ms-excel";\n\n        // Add a header to prompt the user with the download/save dialog\n        HttpContext.Current.Response.AddHeader("Content-Disposition", "attachment; filename=" + Path.GetFileName(filePath));\n\n        // Write the file to the response stream\n        HttpContext.Current.Response.WriteFile(filePath);\n\n        // End the response to avoid any further processing\n        HttpContext.Current.Response.End();\n    }\n    else\n    {\n        // If the file doesn't exist, throw an exception or handle accordingly\n        throw new FileNotFoundException("The specified file was not found.", filePath);\n    }\n}