using System;\nusing System.Data.Linq;\n\npublic class DataContextSwitcher\n{\n    private string productionConnectionString = "your_production_connection_string";\n    private string testConnectionString = "your_test_connection_string";\n\n    public DataContext GetDataContext(bool useProduction)\n    {\n        string connectionString = useProduction ? productionConnectionString : testConnectionString;\n        return new DataContext(connectionString);\n    }\n}\n\n// Usage\nclass Program\n{\n    static void Main()\n    {\n        DataContextSwitcher switcher = new DataContextSwitcher();\n        \n        // Use production database\n        DataContext productionContext = switcher.GetDataContext(true);\n        \n        // Use test database\n        DataContext testContext = switcher.GetDataContext(false);\n    }\n}