using System.Web.Mvc;\nusing Akismet;\n\npublic class SpamController : Controller\n{\n    private const string AkismetApiKey = "your_akismet_api_key";\n    private const string BlogUrl = "http://yourblog.com";\n\n    [HttpPost]\n    public JsonResult CheckForSpam(string userName, string userEmail, string userComment)\n    {\n        var akismet = new AkismetClient(AkismetApiKey, BlogUrl);\n        var comment = new AkismetComment\n        {\n            Blog = BlogUrl,\n            UserIp = Request.UserHostAddress,\n            UserAgent = Request.UserAgent,\n            CommentType = "comment",\n            Author = userName,\n            AuthorEmail = userEmail,\n            Content = userComment\n        };\n\n        bool isSpam = akismet.VerifyKey() && akismet.CommentCheck(comment);\n\n        return Json(new { isSpam = isSpam });\n    }\n}