// Setting the environment variables\nstring myExecutable = "myApp.exe";\nstring server = "myServer";\nstring database = "top_secret_data";\nstring userID = "myUser";\nstring password = "ABC123";\n\nProcessStartInfo startInfo = new ProcessStartInfo(myExecutable);\nstartInfo.EnvironmentVariables["DB_SERVER"] = server;\nstartInfo.EnvironmentVariables["DB_DATABASE"] = database;\nstartInfo.EnvironmentVariables["DB_USERID"] = userID;\nstartInfo.EnvironmentVariables["DB_PASSWORD"] = password;\n\nProcess.Start(startInfo);