using System;\nusing System.Data.Odbc;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "Driver={SQL Server};Server=your_server_name;Database=your_database_name;Trusted_Connection=yes;";\n        using (OdbcConnection connection = new OdbcConnection(connectionString))\n        {\n            connection.Open();\n\n            string xmlData = "<root><item>example</item></root>";  // Your XML data as a string\n            string storedProcedureName = "{CALL YourStoredProcedureName(?)}";\n            \n            using (OdbcCommand command = new OdbcCommand(storedProcedureName, connection))\n            {\n                OdbcParameter xmlParameter = new OdbcParameter("@YourXmlParameterName", OdbcType.NText);\n                xmlParameter.Value = xmlData;\n                command.Parameters.Add(xmlParameter);\n\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}