using System;\nusing System.IO;\nusing System.Security.Cryptography;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        MemoryStream ms = new MemoryStream();\n        byte[] rawPlaintext = Encoding.Unicode.GetBytes("This is annoying!");\n\n        using (Aes aes = new AesManaged())\n        {\n            aes.Padding = PaddingMode.PKCS7;\n            aes.Key = new byte[128 / 8]; // Ensure this key is securely generated/stored\n            aes.IV = new byte[128 / 8];  // Ensure this IV is securely generated/stored\n\n            using (CryptoStream cs = new CryptoStream(ms, aes.CreateEncryptor(), CryptoStreamMode.Write))\n            {\n                cs.Write(rawPlaintext, 0, rawPlaintext.Length);\n                cs.FlushFinalBlock();\n            }\n\n            byte[] encryptedData = ms.ToArray(); // Use ToArray to get the exact encrypted data\n\n            ms = new MemoryStream(encryptedData);\n            using (CryptoStream cs = new CryptoStream(ms, aes.CreateDecryptor(), CryptoStreamMode.Read))\n            {\n                byte[] rawData = new byte[rawPlaintext.Length];\n                int len = cs.Read(rawData, 0, rawPlaintext.Length);\n                string s = Encoding.Unicode.GetString(rawData, 0, len);\n                Console.WriteLine(s);\n            }\n        }\n    }\n}