public class OrderController : Controller\n{\n    private readonly IOrderService _orderService;\n    private readonly IAuthorizationService _authorizationService;\n\n    public OrderController(IOrderService orderService, IAuthorizationService authorizationService)\n    {\n        _orderService = orderService;\n        _authorizationService = authorizationService;\n    }\n\n    [HttpPost]\n    public IActionResult DeleteOrder(int orderId)\n    {\n        var userId = User.Identity.GetUserId(); // Assuming the user is authenticated\n\n        if (_authorizationService.CanDeleteOrder(userId, orderId))\n        {\n            _orderService.DeleteOrder(orderId);\n            return Ok();\n        }\n\n        return Forbid();\n    }\n}