using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class DataAnalyzer\n{\n    private string connectionString = "YourConnectionString";\n\n    public DataTable GetAverageTimeForMethodCalls()\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            string query = "SELECT ApplicationName, MethodName, AVG(TimeInMs) AS AvgTimeInMs FROM YourTableName GROUP BY ApplicationName, MethodName";\n            SqlDataAdapter adapter = new SqlDataAdapter(query, connection);\n            DataTable resultTable = new DataTable();\n            adapter.Fill(resultTable);\n            return resultTable;\n        }\n    }\n}